    public static void main(String[] args) {

        int n = 5;// 5,a,b,c,d,e
        int weight[] = { 0, 4, 5, 6, 2, 2 };// 
        int value[] = { 0, 6, 4, 5, 3, 6 }; // 
        int c = 10; // 
        int state[] = { 0, 0, 0, 0, 0, 0 };// 
        char name[] = { ' ', 'a', 'b', 'c', 'd', 'e' };
        int maxValue = getMaxValue(n, weight, value, state, c);
        System.out.println(" = " + maxValue);
        System.out.print(" ");
        for (int i = 1; i <= 5; i++) {
            if (state[i] == 1) {
                System.out.print(name[i] + "  ");
            }
        }

        // System.out.println();
    }

    /**
     * 
     * @param n
     *            
     * @param weight
     *            0
     * @param value
     *            0
     * @param state
     *            
     * @param c
     *            
     * @return
     */
    public static int getMaxValue(int n, int weight[], int value[], int state[], int c) {
        // n  1 0,1,...n  n+1  0 
        //  2 
        int m[][] = new int[n + 1][c + 1]; // n  0,1,2...(n-1), 0,1,2...C
        int i, j;

        for (i = 0; i <= n; i++) {
            m[i][0] = 0;
        }
        for (j = 0; j <= c; j++) {
            m[0][j] = 0;
        }

        for (i = 1; i <= n; i++) {
            // System.out.println();
            for (j = 1; j <= c; j++) {
                if (j < weight[i]) { // 
                    m[i][j] = m[i - 1][j];
                } else {//  
                    m[i][j] = Math.max(m[i - 1][j - weight[i]] + value[i], m[i - 1][j]);
                }
                // System.out.print("m["+i+"]["+j+"]="+m[i][j]+" ");
                // System.out.print(m[i][j]+" ");
            }
        }

        //  i

        j = c;
        for (i = n; i > 0; i--) {
            if (m[i][j] > m[i - 1][j]) {//  i 
                state[i] = 1;
                j = j - weight[i];
            } else { // 
                state[i] = 0;
            }
        }

        return m[n][c]; // 
    }
