            used[i] = true; // 
            permuteUnique.add(nums[i]); // 
            //  
            dfs(nums, used, permuteUniques, permuteUnique);
            //   
            used[i] = false;
            permuteUnique.remove(permuteUnique.size() - 1);
        }

